import './App.css';
import { useState } from 'react';
import SeuNome from './components/SeuNome';
import Saudacao from './components/Saudacao';

export default function App() {
  const [ name, setName ] = useState()

  return (
    <div className="App">
      <h1>State Lift</h1>
      <SeuNome setName={setName}/>
      <Saudacao name={name}/>
    </div>
  );
}
